'use strict';

var React    = require('react')
var TagField = require('./src')

var tags = ['lang','javascript', 'good', 'the','test','morning' ]
var VALUE = 'web'
var App = React.createClass({

    render: function(){

        var add = function(allTags){
            tags = allTags
            this.setState({})
        }.bind(this)

        var onChange = function(value){
            VALUE = value
            this.setState({})
        }.bind(this)

        return <div style={{position: 'relative', width: '100%', height: '100%'}}>
                <TagField style={{width: 370, padding: 2, margin: 50}}
                    tags={tags} value={VALUE}
                    editableTags={true}
                    selectableTags={true}
                    onChangeTags={add}
                    onChange={onChange}
                    />
            </div>
    }
})

React.render((
    <App />
), document.getElementById('content'))